#!/usr/bin/env bash\n\n# Install Homebrew if not already installed\nif ! command -v brew &> /dev/null; then\n  echo "Installing Homebrew..."\n  /bin/bash -c "$(curl -fsSL https://raw.githubusercontent.com/Homebrew/install/HEAD/install.sh)"\n  \n  # Add Homebrew to PATH\n  if [[ "$(uname -m)" == "arm64" ]]; then\n    echo 'eval "$(/opt/homebrew/bin/brew shellenv)"' >> ~/.zprofile\n    eval "$(/opt/homebrew/bin/brew shellenv)"\n  else\n    echo 'eval "$(/usr/local/bin/brew shellenv)"' >> ~/.zprofile\n    eval "$(/usr/local/bin/brew shellenv)"\n  fi\nfi\n\n# Update Homebrew\nbrew update\n\n###############################################################################\n# Taps                                                                        #\n###############################################################################\necho "Adding Homebrew taps..."\n\nbrew tap homebrew/cask\nbrew tap homebrew/cask-fonts\nbrew tap homebrew/bundle\n\n###############################################################################\n# Terminal Utilities                                                         #\n###############################################################################\necho "Installing terminal utilities..."\n\nbrew install \\n  aria2 \\n  bat \\n  btop \\n  exa \\n  fd \\n  ffmpeg \\n  fzf \\n  htop \\n  jq \\n  lazygit \\n  lazydocker \\n  lnav \\n  ncdu \\n  ranger \\n  ripgrep \\n  tldr \\n  tree \\n  wget \\n  yazi \\n  yt-dlp \\n  zoxide\n\n# System information and monitoring\nbrew install macchina\n\n###############################################################################\n# Development Tools                                                           #\n###############################################################################\necho "Installing development tools..."\n\n# Git and version control\nbrew install git\n\n# DevOps tools\nbrew install \\n  docker \\n  kubectl \\n  helm \\n  k9s \\n  terraform \\n  slimtoolkit/slim/slim\n\n# Text editors and IDE tools\nbrew install \\n  neovim \\n  vim\n\n# Database tools\n# CLI utilities will be installed, DBeaver added to casks below\n\n# Programming languages and package managers\nbrew install python\n\n# Install additional Python tools via pip\npip3 install --user \\n  uv \\n  ruff \\n  pyright \\n  pdoc \\n  commitizen \\n  pre-commit\n\n# Shell utilities\nbrew install nushell\n\n# GPG and security tools\nbrew install gnupg\n\n# Install Rust via rustup\necho "Installing Rust via rustup..."\ncurl --proto '=https' --tlsv1.2 -sSf https://sh.rustup.rs | sh -s -- -y\n\n# Install miniforge3 (Conda)\nbrew install miniforge\n\n# Security and hacking tools\n# brew install \\n#   aircrack-ng \\n#   hashcat \\n#   john \\n#   nmap \\n#   wireshark\n\n# Install Ollama\n# brew install ollama\n\n###############################################################################\n# GUI Applications via Cask                                                  #\n###############################################################################\necho "Installing GUI applications..."\n\n# Fonts\nbrew install --cask \\n  font-meslo-lg-nerd-font \\n  font-fira-code-nerd-font\n\n# Terminal emulators\nbrew install --cask \\n  warp \\n  wezterm\n\n# Development tools\nbrew install --cask \\n  dbeaver-community \\n  visual-studio-code\n\n# Productivity and utilities\nbrew install --cask \\n  alfred \\n  alt-tab \\n  amethyst \\n  appcleaner \\n  bettertouchtool \\n  caffeine \\n  discord \\n  drawio \\n  firefox \\n  hammerspoon \\n  hiddenbar \\n  iina \\n  jan \\n  karabiner-elements \\n  keepassxc \\n  maccy \\n  microsoft-teams \\n  monitor-control \\n  obsidian \\n  orbstack \\n  pandan \\n  pure-paste \\n  qlstephen \\n  quicklook-json \\n  qlmarkdown \\n  raycast \\n  rectangle \\n  shotr \\n  spotify \\n  stats \\n  the-unarchiver \\n  utm \\n  vlc\n\n# Flux (separate due to name format)\nbrew install --cask f-lux\n\n# Install mactex without GUI components\nbrew install --cask mactex-no-gui\n\n# Install mas (Mac App Store CLI) for oneswitch installation\nbrew install mas\n\n# Install OneSwitch if Mac App Store is available\nif command -v mas &> /dev/null; then\n  echo "Installing OneSwitch from Mac App Store..."\n  mas install 1477630474 || echo "Could not install OneSwitch. Check Mac App Store authentication."\nfi\n\n# Check for NVIDIA GPU and install CUDA if found\nif [[ "$(system_profiler SPDisplaysDataType 2>/dev/null | grep -i nvidia)" ]]; then\n  echo "NVIDIA GPU detected, installing CUDA tools..."\n  brew install --cask cuda\nelse\n  echo "No NVIDIA GPU detected, skipping CUDA installation"\nfi\n\n###############################################################################\n# Cleanup                                                                    #\n###############################################################################\necho "Cleaning up..."\nbrew cleanup\n\necho "Homebrew installation complete!"