#!/bin/bash\n\nOPTIONS="Logout\nSuspend\nReboot\nShutdown"\n\nchosen=$(echo -e "$OPTIONS" | rofi -dmenu -i -p "Power Menu:")\n\ncase "$chosen" in\n    "Logout")\n        hyprctl dispatch exit 0\n        ;;\n    "Suspend")\n        systemctl suspend\n        ;;\n    "Reboot")\n        systemctl reboot\n        ;;\n    "Shutdown")\n        systemctl poweroff\n        ;;\nesac\n\nexit 0 