#!/usr/bin/env bash\n# unix-healer.sh - Heal common dotfiles/chezmoi issues on Unix/macOS/Linux\n\nset -e\n\nprintf '\033[36m[Healer] Starting dotfiles healing process...\033[0m\n'\n\n# 1. Ensure chezmoi is installed\nif ! command -v chezmoi >/dev/null 2>&1; then\n  printf '\033[31m[Healer] chezmoi not found. Please install chezmoi first!\033[0m\n'\nelse\n  printf '\033[32m[Healer] chezmoi found.\033[0m\n'\nfi\n\n# 2. Symlink shell profiles\nfor shell in zsh bash; do\n  src="$PWD/config/$shell/${shell}rc"\n  dest="$HOME/.${shell}rc"\n  if [ -f "$src" ]; then\n    if [ ! -L "$dest" ] || [ "$(readlink "$dest")" != "$src" ]; then\n      printf "[Healer] Symlinking $dest to $src\n"\n      ln -sf "$src" "$dest"\n    else\n      printf "[Healer] $dest is already symlinked.\n"\n    fi\n  fi\ndone\n\n# 3. Make scripts executable\nfind "$PWD/scripts/unix" -type f -name '*.sh' -exec chmod +x {} +\nprintf '[Healer] Made all scripts in scripts/unix executable.\n'\n\n# 4. Check for Nerd Font\nif fc-list | grep -qi 'Nerd Font'; then\n  printf '[Healer] Nerd Font is installed.\n'\nelse\n  printf '\033[33m[Healer] Nerd Font not found. Please install a Nerd Font for best prompt experience.\033[0m\n'\nfi\n\nprintf '\033[36m[Healer] Healing complete! Restart your terminal to apply all changes.\033[0m\n' 