#!/bin/bash\n\nSRC="$(dirname "$0")/../../media/wallpapers"\nDEST="$HOME/Pictures/Wallpapers"\n\nif [ ! -L "$DEST" ] && [ ! -d "$DEST" ]; then\n    mkdir -p "$(dirname "$DEST")"\n    ln -s "$SRC" "$DEST"\n    echo "Symlinked $SRC to $DEST"\nelse\n    echo "$DEST already exists or is a symlink."\nfi 